INSERT INTO `tblnovel` (`id`, `sNovel`, `sCover`, `sDes`, `dCreateDay`, `dUpdateDay`, `sProgress`, `iStatus`, `idUser`, `iLicense_Status`, `sLicense`) VALUES
(17, 'Tiên Mộ', 'time_1722136017_file_300.jpg', '&lt;p&gt;Mười vạn năm trước, chư ti&amp;ecirc;n đại chiến, vạn ti&amp;ecirc;n vẫn diệt, ti&amp;ecirc;n đạo đoạn tuyệt, Ti&amp;ecirc;n Giới ở trong ti&amp;ecirc;n mộ san s&amp;aacute;t.&lt;br /&gt;\r\n&lt;br /&gt;\r\nMười vạn năm về sau, Mạc Kim Gi&amp;aacute;o &amp;Uacute;y mang theo [ Sinh Tử Thi&amp;ecirc;n Thư ] gi&amp;aacute;ng l&amp;acirc;m Ti&amp;ecirc;n Giới.&lt;br /&gt;\r\n&lt;br /&gt;\r\n&amp;quot;Trộm mộ thế nhưng l&amp;agrave; một m&amp;ocirc;n nghệ thuật, ca đến n&amp;oacute;i cho c&amp;aacute;c ngươi biết trộm mộ ch&amp;iacute;nh x&amp;aacute;c thao t&amp;aacute;c phương thức.&amp;quot; Lục V&amp;acirc;n nh&amp;igrave;n xem trong ti&amp;ecirc;n mộ luống cuống tay ch&amp;acirc;n Ti&amp;ecirc;n Nh&amp;acirc;n, ngưu kh&amp;iacute; hống hống n&amp;oacute;i. . . .&lt;br /&gt;\r\n&lt;br /&gt;\r\nP/S: Truyện đ&amp;atilde; được chuyển thể th&amp;agrave;nh phim 3D&lt;br /&gt;\r\n&lt;br /&gt;\r\n------ Hệ Thống Tu Luyện ------&lt;br /&gt;\r\n_ Kh&amp;iacute; Cảnh: Luyện Kh&amp;iacute; - Ngưng Kh&amp;iacute; - H&amp;oacute;a Kh&amp;iacute;.&lt;br /&gt;\r\n_ Đan Cảnh: Kim Đan, Mệnh Đan, Nguy&amp;ecirc;n Đan.&lt;br /&gt;\r\n_ Thần Cảnh: Nguy&amp;ecirc;n Thần - Luyện Thần - H&amp;oacute;a Thần.&lt;br /&gt;\r\n_Hư Cảnh: Khuy Hư - Động Hư - Phản Hư.&lt;br /&gt;\r\n_Ch&amp;acirc;n Ti&amp;ecirc;n&lt;br /&gt;\r\n_Thi&amp;ecirc;n Ti&amp;ecirc;n&lt;br /&gt;\r\n_Thượng Ti&amp;ecirc;n&lt;br /&gt;\r\n_Kim Ti&amp;ecirc;n&lt;br /&gt;\r\n_Huyền Ti&amp;ecirc;n&lt;br /&gt;\r\n_Ch&amp;iacute; Ti&amp;ecirc;n&lt;br /&gt;\r\n_Quả Vị: La Thi&amp;ecirc;n Quả Vị - Huyền Thi&amp;ecirc;n Quả Vị - Hỗn Nguy&amp;ecirc;n Quả Vị.&lt;br /&gt;\r\n_Th&amp;ocirc;ng Thi&amp;ecirc;n&lt;br /&gt;\r\n_Nguy&amp;ecirc;n Thủy&lt;br /&gt;\r\n_Th&amp;aacute;i Thượng&lt;br /&gt;\r\n_Th&amp;aacute;i Thanh&lt;br /&gt;\r\n_Ngọc Thanh ( Nh&amp;acirc;n Vương cảnh )&lt;br /&gt;\r\n_Đại đế ( Thượng Thanh cảnh )&lt;br /&gt;\r\nỞ nguy&amp;ecirc;n thủy đại đạo cảnh giới l&amp;agrave;&lt;br /&gt;\r\n_Ti&amp;ecirc;n Thi&amp;ecirc;n đại đạo ứng với Kh&amp;iacute; - Đan - Thần - Hư, 4 cảnh.&lt;br /&gt;\r\n_Kim Đan đại đạo ứng với Ti&amp;ecirc;n cảnh ( từ Ch&amp;acirc;n Ti&amp;ecirc;n tới Ch&amp;iacute; Ti&amp;ecirc;n )&lt;br /&gt;\r\n_Ch&amp;acirc;n Thần cảnh ứng với Th&amp;ocirc;ng Thi&amp;ecirc;n - Nguy&amp;ecirc;n Thủy - Th&amp;aacute;i Thượng cảnh.&lt;br /&gt;\r\n_Thi&amp;ecirc;n Thần l&amp;agrave; Th&amp;aacute;i Thanh cảnh&lt;br /&gt;\r\n_Thần Vương l&amp;agrave; Ngọc Thanh cảnh&lt;br /&gt;\r\n_Thi&amp;ecirc;n Địa cảnh (đại thần th&amp;ocirc;ng giả ) l&amp;agrave; Đại Đế cảnh.&lt;br /&gt;\r\n_Hỗn Độn cảnh (Ti&amp;ecirc;n thi&amp;ecirc;n ma thần ) ch&amp;iacute;nh l&amp;agrave; si&amp;ecirc;u việt đại đế.&lt;br /&gt;\r\n_Tạo Vật Chủ&lt;br /&gt;\r\n_Bất Hủ Chi Vương&lt;br /&gt;\r\n_Vĩnh Hằng Chi Chủ&lt;br /&gt;\r\nNh&amp;acirc;n đạo th&amp;agrave;nh lập th&amp;igrave; cảnh giới giống với Nguy&amp;ecirc;n thủy đại đạo nhưng l&amp;agrave; do nh&amp;acirc;n tộc l&amp;agrave;m chủ l&amp;ecirc;n đổi t&amp;ecirc;n từ thần th&amp;agrave;nh nh&amp;acirc;n ( Ch&amp;acirc;n Thần - Ch&amp;acirc;n Nh&amp;acirc;n, Thi&amp;ecirc;n Thần - Thi&amp;ecirc;n Nh&amp;acirc;n, Thần Vương - Nh&amp;acirc;n Vương ).&lt;br /&gt;\r\nQuả Vị cảnh giới xuất hiện khi nh&amp;acirc;n đạo sụp đổ v&amp;agrave; Ti&amp;ecirc;n đạo th&amp;agrave;nh lập.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Nguồn:&amp;nbsp;https://metruyencv.com/tim-kiem?q=Ti%C3%AAn%20M%E1%BB%99&lt;/strong&gt;&lt;/p&gt;', '2024-11-01 02:26:03', '2024-11-16 02:29:32', 1, 1, 40, 1, 'time_1722136017_file_banquyen.pdf'),
(18, 'Thanh Sơn', 'time_1722137318_file_Thanh Sơn.jpg', '&lt;p&gt;Lạc th&amp;agrave;nh trong m&amp;agrave;n đ&amp;ecirc;m tuấn m&amp;atilde; c&amp;ugrave;ng thiếu ni&amp;ecirc;n lang, m&amp;oacute;ng ngựa tại b&amp;agrave;n đ&amp;aacute; xanh b&amp;ecirc;n tr&amp;ecirc;n giẫm ra cộc cộc tiếng vang.&lt;br /&gt;\r\n&lt;br /&gt;\r\nHắn phảng phất Thuyết Thư ti&amp;ecirc;n sinh cố sự b&amp;ecirc;n trong nh&amp;acirc;n vật, theo m&amp;acirc;y th&amp;aacute;c nước b&amp;ecirc;n trong đến, hướng trong giang hồ chỗ đi, đi tới thanh sơn, xem r&amp;aacute;ng chiều lặn về ph&amp;iacute;a t&amp;acirc;y.&lt;br /&gt;\r\n&lt;br /&gt;\r\nNếu ngươi hỏi, ai l&amp;agrave; n&amp;agrave;y trong giang hồ bất quy kh&amp;aacute;ch?&lt;br /&gt;\r\n&lt;br /&gt;\r\nHắn sẽ đ&amp;aacute;p, thanh phong, trăng s&amp;aacute;ng, ta.&lt;br /&gt;\r\n&lt;br /&gt;\r\n. . .&lt;br /&gt;\r\n&lt;br /&gt;\r\nĐ&amp;acirc;y c&amp;oacute; lẽ l&amp;agrave; một c&amp;aacute;i d&amp;agrave;i đằng đẵng chuyện xưa, đợi ta chậm r&amp;atilde;i n&amp;oacute;i&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Nguồn:&amp;nbsp;https://metruyencv.com/truyen/thanh-son-1&lt;/strong&gt;&lt;/p&gt;', '2024-11-16 02:29:40', '2024-11-01 11:27:17', 1, 1, 40, 1, 'time_1722137318_file_banquyen.pdf'),
(19, 'Đại Đạo Chi Thượng', 'time_1722138076_file_Đại Đạo Chi Thượng.jpg', '&lt;p&gt;Gia gia của ta rất cổ qu&amp;aacute;i, hắn mỗi ng&amp;agrave;y cho m&amp;igrave;nh d&amp;acirc;ng hương, đứng tại trước linh vị của m&amp;igrave;nh ăn ngọn nến.&lt;br /&gt;\r\n&lt;br /&gt;\r\nNgười trong th&amp;ocirc;n đều rất sợ gia gia.&lt;br /&gt;\r\n&lt;br /&gt;\r\nTa cũng rất sợ gia gia.&lt;br /&gt;\r\n&lt;br /&gt;\r\nVề sau ta mới ph&amp;aacute;t hiện, bọn hắn sợ sệt kh&amp;ocirc;ng phải gia gia, m&amp;agrave; l&amp;agrave; ta.&lt;br /&gt;\r\n&lt;br /&gt;\r\nGia gia cũng rất sợ ta.&lt;br /&gt;\r\n&lt;br /&gt;\r\n. . .&lt;br /&gt;\r\n&lt;br /&gt;\r\nTa gọi Trần Thực.&lt;br /&gt;\r\n&lt;br /&gt;\r\nTrần trong th&amp;agrave;nh thật, Thực trong đ&amp;agrave;ng ho&amp;agrave;ng .&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Nguồn:&amp;nbsp;https://metruyencv.com/truyen/dai-dao-chi-thuong&lt;/strong&gt;&lt;/p&gt;', '2024-11-16 02:29:48', '2024-11-01 11:39:23', 1, 1, 41, 1, 'time_1722138076_file_banquyen.pdf'),
(20, 'Running Man: Ai Mời Hắn Đến? Hắn Thế Nhưng Là Đạo Sĩ A', 'time_1722138844_file_123.jpg', '&lt;p&gt;Biệt danh &amp;laquo; Running Man: Để ngươi khi kh&amp;aacute;ch qu&amp;yacute;, kh&amp;ocirc;ng c&amp;oacute; để ngươi c&amp;aacute;ch l&amp;agrave;m! &amp;raquo;&lt;br /&gt;\r\n&lt;br /&gt;\r\nĐạo sĩ L&amp;acirc;m Thần xuy&amp;ecirc;n việt thế giới song song, trở th&amp;agrave;nh Running Man ngoại tr&amp;agrave;ng kh&amp;aacute;ch qu&amp;yacute;, ngo&amp;agrave;i &amp;yacute; muốn giải tỏa thần cấp lựa chọn hệ thống!&lt;br /&gt;\r\n&lt;br /&gt;\r\nĐen kịt trong m&amp;ecirc; cung.&lt;br /&gt;\r\n&lt;br /&gt;\r\nLựa chọn &amp;laquo; sử dụng ch&amp;uacute; thuật truy tung minh tinh vị tr&amp;iacute;, ban thưởng: Tuổi thọ +10 năm! &amp;raquo;&lt;br /&gt;\r\n&lt;br /&gt;\r\nLựa chọn &amp;laquo; sử dụng khinh c&amp;ocirc;ng x&amp;eacute; nh&amp;atilde;n hiệu, ban thưởng: V&amp;ocirc; hạn thể lực! &amp;raquo;&lt;br /&gt;\r\n&lt;br /&gt;\r\nLựa chọn &amp;laquo; sử dụng t&amp;iacute;nh mệnh ph&amp;aacute; giải c&amp;acirc;u đố, ban thưởng: Thần cấp may mắn kỹ năng! &amp;raquo;&lt;br /&gt;\r\n&lt;br /&gt;\r\n. . .&lt;br /&gt;\r\n&lt;br /&gt;\r\nL&amp;uacute;c đầu, mọi người cho l&amp;agrave; hắn chỉ l&amp;agrave; c&amp;aacute;i vật l&amp;agrave;m nền d&amp;ugrave;ng phổ th&amp;ocirc;ng tiểu nghệ nh&amp;acirc;n!&lt;br /&gt;\r\n&lt;br /&gt;\r\nKh&amp;ocirc;ng nghĩ tới!&lt;br /&gt;\r\n&lt;br /&gt;\r\nBởi v&amp;igrave; hắn kh&amp;ocirc;ng hợp th&amp;oacute;i thường thao t&amp;aacute;c, nh&amp;acirc;n kh&amp;iacute; một đường tăng vọt, hắn trực tiếp từ ngoại tr&amp;agrave;ng kh&amp;aacute;ch qu&amp;yacute;, trở th&amp;agrave;nh thường tr&amp;uacute; kh&amp;aacute;ch qu&amp;yacute;!&lt;br /&gt;\r\n&lt;br /&gt;\r\nNgười xem gọi thẳng: Kh&amp;ocirc;ng c&amp;oacute; L&amp;acirc;m Thần ch&amp;uacute;ng ta kh&amp;ocirc;ng nh&amp;igrave;n tiết mục!&lt;br /&gt;\r\n&lt;br /&gt;\r\nMột đ&amp;aacute;m minh tinh cũng đều bị sợ ng&amp;acirc;y người!&lt;br /&gt;\r\n&lt;br /&gt;\r\nĐặng Sao: Trời ơi! Thế n&amp;agrave;y sao lại l&amp;agrave; kh&amp;aacute;ch qu&amp;yacute; a, đ&amp;acirc;y kh&amp;ocirc;ng phải liền l&amp;agrave; ti&amp;ecirc;n nh&amp;acirc;n sao!&lt;br /&gt;\r\n&lt;br /&gt;\r\nTrịnh Khải: Đừng c&amp;oacute; lại gọi ta tiểu b&amp;aacute;o săn, tiểu b&amp;aacute;o săn cũng sợ ph&amp;ugrave; ch&amp;uacute; Định Th&amp;acirc;n Thuật a!&lt;br /&gt;\r\n&lt;br /&gt;\r\nL&amp;yacute; th&amp;acirc;n: Kh&amp;ocirc;ng s&amp;aacute;nh bằng, căn bản kh&amp;ocirc;ng s&amp;aacute;nh bằng! Hắn nh&amp;igrave;n cao cao gầy g&amp;ograve;, kh&amp;iacute; lực l&amp;agrave;m sao lớn như vậy a!&lt;br /&gt;\r\n&lt;br /&gt;\r\nTrần Hạ: Ai mời hắn đến? Hắn nhưng l&amp;agrave; đạo sĩ a!&lt;br /&gt;\r\n&lt;br /&gt;\r\nK&amp;ecirc; ca: Ai u, ngươi l&amp;agrave;m g&amp;igrave;!&lt;br /&gt;\r\n&lt;br /&gt;\r\nBạch Lộ: Oa tiểu ca ca rất đẹp a!&lt;br /&gt;\r\n&lt;br /&gt;\r\nNhiệt Ba: X&amp;eacute; c&amp;aacute;i nh&amp;atilde;n hiệu đem ph&amp;ugrave; ch&amp;uacute; đều m&amp;oacute;c ra, ch&amp;uacute;ng ta đều ngốc!&lt;br /&gt;\r\n&lt;br /&gt;\r\nDương Mật: Đ&amp;acirc;y c&amp;oacute; phải hay kh&amp;ocirc;ng l&amp;agrave; c&amp;oacute; ch&amp;uacute;t qu&amp;aacute; bất hợp l&amp;yacute;?&lt;br /&gt;\r\n&lt;br /&gt;\r\nBaby: Đạo diễn! Kh&amp;ocirc;ng c&amp;ocirc;ng bằng! Hắn một mực l&amp;agrave; qu&amp;aacute;n qu&amp;acirc;n!&lt;br /&gt;\r\n&lt;br /&gt;\r\nHậu trường, đạo diễn xoa xoa c&amp;aacute;i tr&amp;aacute;n mồ h&amp;ocirc;i: Ai mời ngoại tr&amp;agrave;ng kh&amp;aacute;ch qu&amp;yacute;?&lt;br /&gt;\r\n&lt;br /&gt;\r\nN&amp;oacute;i xong d&amp;ugrave;ng để vật l&amp;agrave;m nền minh tinh, hắn vậy m&amp;agrave; trực tiếp th&amp;agrave;nh đỉnh chảy!&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Nguồn :&amp;nbsp;https://metruyencv.com/truyen/running-man-ai-moi-han-den-han-the-nhung-la-dao-si-a&lt;/strong&gt;&lt;/p&gt;', '2024-11-16 03:14:24', '2024-11-16 03:14:24', 1, 1, 40, 1, 'time_1722138844_file_banquyen.pdf'),
(21, 'Từ Hài Nhi Bắt Đầu Nhập Đạo', 'time_1722139233_file_Từ Hài Nhi Bắt Đầu Nhập Đạo.jpg', '&lt;p&gt;Loạn thế cầu sinh, thịnh thế cầu t&amp;ecirc;n.&lt;br /&gt;\r\n&lt;br /&gt;\r\nD&amp;atilde;i dầu sương gi&amp;oacute; Đại Vũ triều vẫn ở v&amp;agrave;o thịnh thế thời k&amp;igrave;.&lt;br /&gt;\r\n&lt;br /&gt;\r\nT&amp;acirc;y Bắc, c&amp;oacute; thiếu ni&amp;ecirc;n đi bộ ch&amp;iacute;n ng&amp;agrave;n dặm, độc th&amp;acirc;n trảm đại y&amp;ecirc;u, tranh một đời c&amp;ocirc;ng danh.&lt;br /&gt;\r\n&lt;br /&gt;\r\nLong Hồ, c&amp;oacute; Bất Thế T&amp;ocirc;ng Sư đi nh&amp;acirc;n gian, khai t&amp;ocirc;ng lập ph&amp;aacute;i, mu&amp;ocirc;n đời lưu danh.&lt;br /&gt;\r\n&lt;br /&gt;\r\nĐế đ&amp;ocirc;, c&amp;oacute; Th&amp;aacute;nh Nh&amp;acirc;n truyền đạo ng&amp;agrave;n vạn, gi&amp;aacute;o đồ trải rộng c&amp;aacute;c ch&amp;acirc;u, lưu danh thi&amp;ecirc;n cổ.&lt;br /&gt;\r\n&lt;br /&gt;\r\nM&amp;agrave; tại Thanh Ch&amp;acirc;u th&amp;agrave;nh b&amp;ecirc;n trong, một chỗ trong tiểu viện.&lt;br /&gt;\r\n&lt;br /&gt;\r\nC&amp;oacute; người lại chỉ muốn y&amp;ecirc;n lặng, hạ xong mỗi một b&amp;agrave;n cờ.&lt;br /&gt;\r\n&lt;br /&gt;\r\n. . .&lt;br /&gt;\r\n&lt;br /&gt;\r\nKiếm l&amp;ecirc;n Long Y&amp;ecirc;n ba ng&amp;agrave;n dặm, một kiếm ch&amp;eacute;m hết Cửu Ch&amp;acirc;u th&amp;agrave;nh!&lt;br /&gt;\r\n&lt;br /&gt;\r\nThế gian vạn ph&amp;aacute;p, duy ta, nhập đạo.&lt;br /&gt;\r\n&lt;br /&gt;\r\n. . .&lt;br /&gt;\r\n&lt;br /&gt;\r\nQuyển s&amp;aacute;ch lại t&amp;ecirc;n &amp;laquo; vạn thế chi danh &amp;raquo; &amp;laquo; kh&amp;ocirc;ng thấy ngươi luyện kiếm, ngươi l&amp;agrave;m sao lại th&amp;agrave;nh Kiếm Thần &amp;raquo; &amp;laquo; Hạo Thi&amp;ecirc;n Đế &amp;raquo;&lt;br /&gt;\r\n&lt;br /&gt;\r\n【 nhẹ nh&amp;otilde;m h&amp;agrave;i hước ] 【 v&amp;otilde; đạo bảng ] 【 nghệ thuật đại sư ]&lt;br /&gt;\r\n&lt;br /&gt;\r\n☆☆☆ Hệ thống tu luyện ☆☆☆&lt;br /&gt;\r\n1- Th&amp;ocirc;ng Lực&lt;br /&gt;\r\n2- Chu Thi&amp;ecirc;n&lt;br /&gt;\r\n3- Kế Hồn&lt;br /&gt;\r\n4- Thần Du&lt;br /&gt;\r\n5- Thập Ngũ L&amp;yacute;&lt;br /&gt;\r\n6- Thi&amp;ecirc;n Nh&amp;acirc;n&lt;br /&gt;\r\n7- Tam Bất Hủ (bất ph&amp;ocirc;i, bất diệt, bất suy)&lt;br /&gt;\r\n8- Tứ Lập (đạo t&amp;acirc;m, nghịch mệnh, tuyệt học, th&amp;aacute;i b&amp;igrave;nh đạo cảnh)&lt;br /&gt;\r\n9- Văn Đạo&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Nguồn:&amp;nbsp;https://metruyencv.com/truyen/tu-hai-nhi-bat-dau-nhap-dao&lt;/strong&gt;&lt;/p&gt;', '2024-11-16 02:30:05', '2024-11-01 11:39:19', 1, 1, 41, 1, 'time_1722139233_file_banquyen.pdf'),
(22, 'Hoàng Hôn Phân Giới', 'time_1722139478_file_Hoàng Hôn Phân Giới.jpg', '&lt;p&gt;Chết thật tốt, bị người cưỡng &amp;eacute;p sống lại.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Nguồn:&amp;nbsp;https://metruyencv.com/truyen/hoang-hon-phan-gioi&lt;/strong&gt;&lt;/p&gt;', '2024-11-16 02:30:12', '2024-11-01 11:39:21', 1, 1, 41, 1, 'time_1722139478_file_banquyen.pdf'),
(23, 'Thôn Phệ Tinh Không 2 Khởi Nguyên Đại Lục', 'time_1722139655_file_Thôn Phệ Tinh Không 2 Khởi Nguyên Đại Lục.jpg', '&lt;p&gt;La Phong mang theo Giới Th&amp;uacute; Ma La T&amp;aacute;t x&amp;ocirc;ng qua lu&amp;acirc;n hồi, đi tới Khởi Nguy&amp;ecirc;n đại lục...&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Nguồn:&amp;nbsp;https://metruyencv.com/truyen/thon-phe-tinh-khong-2-khoi-nguyen-dai-luc&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;', '2024-11-16 02:30:19', '2024-11-02 11:28:06', 1, 1, 40, 1, 'time_1722139655_file_banquyen.pdf'),
(24, 'Võng Du Chi Ta Có 10 Lần Tốc Độ Đánh', 'time_1722139820_file_Võng Du Chi Ta Có 10 Lần Tốc Độ Đánh.jpg', '&lt;p&gt;C&amp;ocirc;ng Nguy&amp;ecirc;n 2020 năm, Li&amp;ecirc;n Hợp Quốc khai th&amp;aacute;c V&amp;otilde;ng Du 【 Thần Vực 】 chấn động Open Beta, kim tệ hối đo&amp;aacute;i hiện thực tiền, hấp dẫn to&amp;agrave;n cầu v&amp;ocirc; số người chơi. Thế giới c&amp;aacute;c quốc gia, v&amp;igrave; tranh đoạt du h&amp;iacute; t&amp;agrave;i nguy&amp;ecirc;n đ&amp;middot;&amp;aacute;nh đ&amp;middot;ập t&amp;agrave;n nhẫn.&lt;br /&gt;\r\n&lt;br /&gt;\r\nTần Phong l&amp;agrave; một g&amp;atilde; 【 Thần Vực 】 người chơi, bất qu&amp;aacute;, hắn sở hữu hạng nhất si&amp;ecirc;u thần thi&amp;ecirc;n ph&amp;uacute; -- thập bội tốc độ đ&amp;aacute;nh!&lt;br /&gt;\r\n&lt;br /&gt;\r\nL&amp;agrave;m kh&amp;aacute;c người chơi vẫn c&amp;ograve;n ở chậm r&amp;atilde;i đ&amp;aacute;nh tiểu qu&amp;aacute;i l&amp;uacute;c, Tần Phong thập bội tốc độ đ&amp;aacute;nh, đ&amp;atilde; ung dung c&amp;aacute;ch l&amp;agrave;m rơi BOSS.&lt;br /&gt;\r\n&lt;br /&gt;\r\nMỹ nữ hội trưởng: &amp;quot;Phong ca, thập bội tốc độ cảm gi&amp;aacute;c, ng&amp;ocirc;, thực sự thật sự rất tốt thoải m&amp;aacute;i đ&amp;acirc;u. . .&amp;quot;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Nguồn:&amp;nbsp;https://metruyencv.com/truyen/vong-du-chi-ta-co-10-lan-toc-do-danh&lt;/strong&gt;&lt;/p&gt;', '2024-11-16 02:30:28', '2024-11-01 11:39:25', 1, 1, 41, 1, 'time_1722139820_file_banquyen.pdf'),
(25, 'Từ Arknights Bắt Đầu Xây Dựng Thế Lực', 'time_1722140206_file_Từ Arknights Bắt Đầu Xây Dựng Thế Lực.jpg', '&lt;p&gt;L&amp;acirc;u l&amp;acirc;u muốn đổi mới m&amp;ocirc; tả m&amp;agrave; chả biết viết g&amp;igrave;, th&amp;ocirc;i th&amp;igrave; t&amp;oacute;m tắt tạm vậy.&lt;br /&gt;\r\nMap ch&amp;iacute;nh: Terra (Arknights), bắt đầu từ khoảng 20 năm trước c&amp;aacute;c sự kiện ch&amp;iacute;nh.&lt;br /&gt;\r\nTương lai sẽ bay qua bay lại c&amp;aacute;c thế giới kh&amp;aacute;c, nhưng sẽ chỉ giới hạn trong anime, manga. T&amp;aacute;c kh&amp;ocirc;ng viết map truyện Trung. Nguy&amp;ecirc;n nh&amp;acirc;n? Do t&amp;aacute;c kh&amp;ocirc;ng th&amp;iacute;ch, với lại nh&amp;eacute;t truyện trung v&amp;agrave;o manga, anime n&amp;oacute; kỳ kỳ sao ấy, n&amp;ecirc;n th&amp;ocirc;i, kh&amp;ocirc;ng truyện trung nh&amp;eacute;.&lt;br /&gt;\r\n: )&lt;br /&gt;\r\nBản đồ Terra: https://i.pinimg.com/564x/de/18/2c/de182c676fc22a2e930f34b3be8ccc90.jpg&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Nguồn truyện:&amp;nbsp;https://metruyencv.com/truyen/tu-arknights-bat-dau-xay-dung-the-luc&lt;/strong&gt;&lt;/p&gt;', '2024-11-16 02:30:37', '2024-11-01 11:27:50', 1, 1, 40, 1, 'time_1722140206_file_banquyen.pdf'),
(26, 'Xuyên Về Cổ Đại Làm Tiểu Cô Nương Lợi Hại', 'time_1731615156_file_Xuyên về cổ đại làm tiểu cô nương.png', '&lt;p&gt;L&amp;ecirc; Tường bưng một m&amp;acirc;m lớn đựng đầy thức ăn bỏ l&amp;ecirc;n tr&amp;ecirc;n b&amp;agrave;n, nh&amp;igrave;n một b&amp;agrave;n đồ ăn đầy tr&amp;agrave;n m&amp;agrave; trong l&amp;ograve;ng cũng cảm thấy thỏa m&amp;atilde;n.&lt;br /&gt;\r\n&lt;br /&gt;\r\nTuy n&amp;oacute;i vận mệnh đối với c&amp;ocirc; c&amp;oacute; hơi bất c&amp;ocirc;ng, từ nhỏ đ&amp;atilde; mất đi người th&amp;acirc;n, lại bị ung thư dạ d&amp;agrave;y, nhưng c&amp;ocirc; vẫn lu&amp;ocirc;n c&amp;oacute; tri kỉ tốt bầu bạn ở b&amp;ecirc;n cạnh, sự nghiệp cũng thuận buồm xu&amp;ocirc;i gi&amp;oacute;, gần nửa đời người cũng sống trong vui sướng.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;ldquo;Kiều Kiều, cậu mau nếm thử đi!&amp;rdquo;&lt;br /&gt;\r\n&lt;br /&gt;\r\nL&amp;acirc;m Kiều đem sự chua s&amp;oacute;t nhịn xuống, sau đ&amp;oacute; đưa tay tiếp nhận chiếc đũa, cực k&amp;igrave; nể t&amp;igrave;nh m&amp;agrave; gắp v&amp;agrave;i gắp đồ ăn, một b&amp;ecirc;n v&amp;ugrave;i đầu ăn một b&amp;ecirc;n lại khen ngợi kĩ năng nấu nướng của c&amp;ocirc; bạn m&amp;igrave;nh. Nước mắt rơi cả v&amp;agrave;o trong b&amp;aacute;t m&amp;agrave; c&amp;ocirc; cũng kh&amp;ocirc;ng d&amp;aacute;m ngẩng đầu, bởi v&amp;igrave; c&amp;ocirc; sợ bị bạn m&amp;igrave;nh nh&amp;igrave;n thấy.&lt;br /&gt;\r\n&lt;br /&gt;\r\nR&amp;otilde; r&amp;agrave;ng mới c&amp;oacute; 28 tuổi, c&amp;ograve;n trẻ như vậy, m&amp;agrave; đ&amp;atilde; mắc bệnh ung thư thời k&amp;igrave; cuối, c&amp;ocirc; cứ trơ mắt nh&amp;igrave;n bạn tốt như một đ&amp;oacute;a hoa từ từ kh&amp;ocirc; h&amp;eacute;o m&amp;agrave; kh&amp;ocirc;ng c&amp;oacute; c&amp;aacute;ch n&amp;agrave;o. Mỗi khi nh&amp;igrave;n thấy đỉnh đầu trọc lốc của L&amp;ecirc; Tường l&amp;agrave; c&amp;ocirc; liền kh&amp;oacute; chịu như muốn đ&amp;ograve;i mạng.&lt;/p&gt;\r\n\r\n&lt;p&gt;Nguồn:&amp;nbsp;https://truyenfull.io/xuyen-ve-co-dai-lam-tieu-co-nuong-loi-hai/chuong-1&lt;/p&gt;', '2024-11-15 03:12:36', '2024-11-15 03:12:36', 1, 1, 42, 1, 'time_1731610646_file_time_1722136017_file_banquyen.pdf'),
(27, 'Cố Tiểu Tây', 'time_1731698000_file_cố tiểu tây.jpg', '&lt;p&gt;&amp;quot;Cố Tiểu T&amp;acirc;y, c&amp;ocirc; thật kh&amp;ocirc;ng biết xấu hổ, lớn l&amp;ecirc;n th&amp;agrave;nh bộ dạng n&amp;agrave;y c&amp;ograve;n d&amp;aacute;m d&amp;acirc;y dưa với anh t&amp;ocirc;i? C&amp;ocirc; cũng kh&amp;ocirc;ng soi mặt v&amp;agrave;o trong nước tiểu m&amp;agrave; xem bản th&amp;acirc;n m&amp;igrave;nh!&amp;quot;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&amp;quot;Nh&amp;agrave; t&amp;ocirc;i l&amp;agrave; căn ch&amp;iacute;nh mi&amp;ecirc;u hồng, gia đ&amp;igrave;nh chiến sĩ thi đua quang vinh, c&amp;ocirc; th&amp;igrave; sao? &amp;Ocirc;ng nội c&amp;ocirc; l&amp;agrave; địa chủ! Mẹ c&amp;ocirc; cũng kh&amp;ocirc;ng cần c&amp;ocirc;, chạy theo người trong th&amp;agrave;nh, cha c&amp;ocirc; anh trai c&amp;ocirc; cũng đều l&amp;agrave; kẻ đầu đường x&amp;oacute; chợ, c&amp;ograve;n c&amp;oacute; mặt mũi bu b&amp;aacute;m anh t&amp;ocirc;i?&amp;quot;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&amp;quot;Mau c&amp;uacute;t! Nếu kh&amp;ocirc;ng t&amp;ocirc;i sẽ lấy ph&amp;acirc;n tr&amp;acirc;u giội đầu c&amp;ocirc;!&amp;quot;&lt;/p&gt;\r\n\r\n&lt;p&gt;Nguồn:&amp;nbsp;https://truyenfull.io/co-tieu-tay/chuong-1&lt;/p&gt;', '2024-11-16 02:14:19', '2024-11-16 02:14:19', 1, 1, 42, 1, 'time_1731698000_file_time_1722136017_file_banquyen.pdf'),
(28, 'Từ giới giải trí đến nhà giàu số 1', 'time_1731701233_file_Từ giới giải trí.jpg', '&lt;p&gt;Dương Lỗi đột nhi&amp;ecirc;n nhận được một hệ thống đầy b&amp;iacute; ẩn, cho ph&amp;eacute;p anh đổi to&amp;agrave;n bộ đồ vật bằng danh vọng. Mặc d&amp;ugrave; anh theo chủ nghĩa v&amp;ocirc; thần, nhưng sự xuất hiện của hệ thống đ&amp;atilde; đảo lộn tưởng tượng của anh.&lt;br /&gt;\r\n&lt;br /&gt;\r\nMột ng&amp;agrave;y, khi anh đang học tại đại học, anh đột nhi&amp;ecirc;n bị ngất xỉu v&amp;agrave; sau đ&amp;oacute; nhận được hệ thống. Điều n&amp;agrave;y dẫn anh đến suy nghĩ rằng hai sự kiện n&amp;agrave;y c&amp;oacute; li&amp;ecirc;n quan đến nhau.&lt;br /&gt;\r\n&lt;br /&gt;\r\nKhi anh thấy những biểu tượng tr&amp;ecirc;n giao diện thay đổi, anh nhanh ch&amp;oacute;ng nhận thấy rằng anh c&amp;oacute; thể tiến v&amp;agrave;o một thế giới tiểu thuyết. Trong gi&amp;acirc;y l&amp;aacute;t, anh được đưa đến một thế giới ho&amp;agrave;n to&amp;agrave;n mới.&lt;/p&gt;\r\n\r\n&lt;p&gt;Nguồn:&amp;nbsp;https://truyenfull.io/tu-gioi-giai-tri-den-nha-giau-so-1/&lt;/p&gt;', '2024-11-16 03:13:25', '2024-11-16 03:13:25', 1, 1, 45, 1, 'time_1731701233_file_time_1722140206_file_banquyen.pdf'),
(29, 'Ai Nói Công Pháp Ta Luyện Sai', 'time_1732181752_file_ai nói công pháp ta tu luyện sai.jpg', '&lt;p&gt;&amp;Aacute;nh nắng rực rỡ, tiếng ve r&amp;acirc;m ran, tr&amp;ecirc;n s&amp;acirc;n b&amp;oacute;ng rổ đang diễn ra một trận đấu kịch t&amp;iacute;nh.&lt;br /&gt;\r\n&lt;br /&gt;\r\nTiếng gi&amp;agrave;y ma s&amp;aacute;t với s&amp;agrave;n nhựa, mồ h&amp;ocirc;i vung v&amp;atilde;i, b&amp;oacute;ng rổ đập xuống s&amp;acirc;n, tr&amp;agrave;n ngập hormon của tuổi dậy th&amp;igrave;.&lt;br /&gt;\r\n&lt;br /&gt;\r\n&amp;ldquo;Ối giồi &amp;ocirc;i, ối giồi &amp;ocirc;i, Kha Dục đang l&amp;agrave;m g&amp;igrave; thế!&amp;rdquo;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&amp;ldquo;Aaaaaa, cậu ấy kh&amp;ocirc;ng định cởi &amp;aacute;o ra đ&amp;oacute; chứ!&amp;rdquo;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&amp;ldquo;Mẹ ơi! Con kh&amp;ocirc;ng d&amp;aacute;m nh&amp;igrave;n nữa!&amp;rdquo;&lt;br /&gt;\r\nNguồn:&amp;nbsp;https://truyenfull.io/ca-cuoc-moc-khau-ngan/chuong-1/?utm_source=truyenfull_io&amp;amp;utm_campaign=truyenfullio_boxhothome&lt;/p&gt;', '2024-11-21 18:08:08', '2024-11-21 18:08:08', 1, 1, 45, 1, 'time_1732181752_file_time_1722136017_file_banquyen.pdf'),
(30, 'Thần Đạo Đan Tôn', 'time_1732182500_file_Đan tôn.jpg', '&lt;p&gt;Cường giả tuyệt thế , một đời Đan Đế Lăng H&amp;agrave;n v&amp;igrave; truy cầu con đường th&amp;agrave;nh thần m&amp;agrave; Vẫn Lạc , vạn năm sau khi mang 《 Bất Diệt Thi&amp;ecirc;n Kinh 》 trọng sinh với c&amp;ugrave;ng t&amp;ecirc;n thiếu ni&amp;ecirc;n , từ nay về sau phong v&amp;acirc;n dũng động , c&amp;ugrave;ng đương đại v&amp;ocirc; số thi&amp;ecirc;n t&amp;agrave;i tranh phong , mở lại truyền kỳ đường , vạn cổ Chư Thi&amp;ecirc;n ta cực mạnh !&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;HỆ THỐNG TU LUYỆN :&lt;/p&gt;\r\n\r\n&lt;p&gt;Phần 1:&lt;/p&gt;\r\n\r\n&lt;p&gt;____________Ph&amp;ugrave; Du Giới (Tiểu Thế Giới) :&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;1-Luyện Thể : sơ kỳ (T1 -&amp;gt; T3) - trung kỳ (T4 -&amp;gt; T6) - hậu kỳ (T7 -&amp;gt; T9)&lt;/p&gt;\r\n\r\n&lt;p&gt;2-Tụ Nguy&amp;ecirc;n : sơ kỳ (T1 -&amp;gt; T3) - trung kỳ (T4 -&amp;gt; T6) - hậu kỳ (T7 -&amp;gt; T9) - đại vi&amp;ecirc;n m&amp;atilde;n(ẩn) (T10)&lt;/p&gt;\r\n\r\n&lt;p&gt;3-Dũng Tuyền : sơ kỳ (T1 -&amp;gt; T3) - trung kỳ (T4 -&amp;gt; T6) - hậu kỳ (T7 -&amp;gt; T9)&lt;/p&gt;\r\n\r\n&lt;p&gt;4-Linh Hải : sơ kỳ (T1 -&amp;gt; T3) - trung kỳ (T4 -&amp;gt; T6) - hậu kỳ (T7 -&amp;gt; T9)&lt;/p&gt;\r\n\r\n&lt;p&gt;Nguồn:&amp;nbsp;https://lightnovel.com.vn/truyen/than-dao-dan-ton&lt;/p&gt;', '2024-11-21 18:08:03', '2024-11-21 18:08:03', 1, 1, 45, 1, 'time_1732182500_file_time_1722136017_file_banquyen.pdf'),
(31, 'Đấu Phá Thương Khung', 'time_1732183033_file_đấu phá thương khung.jpg', '&lt;p&gt;&amp;ldquo;Đấu chi lực, tam đoạn&amp;rdquo;&lt;/p&gt;\r\n\r\n&lt;p&gt;Nh&amp;igrave;n trắc nghiệm ma thạch c&amp;oacute; ch&amp;uacute;t ch&amp;oacute;i mắt năm chữ, thiếu ni&amp;ecirc;n mặt kh&amp;ocirc;ng ch&amp;uacute;t thay đổi, thần sắc tự giễu, nắm chặt tay, bởi v&amp;igrave; d&amp;ugrave;ng lực qu&amp;aacute; mạnh l&amp;agrave;m m&amp;oacute;ng tay đ&amp;acirc;m thật s&amp;acirc;u v&amp;agrave;o trong l&amp;ograve;ng b&amp;agrave;n tay, mang đến một trận đau đớn&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;ldquo;Ti&amp;ecirc;u Vi&amp;ecirc;m, đấu lực, tam đoạn! Cấp bậc: Cấp thấp!&amp;rdquo;.&lt;/p&gt;\r\n\r\n&lt;p&gt;B&amp;ecirc;n cạnh trắc nghiệm ma thạch, một vị trung ni&amp;ecirc;n nam tử, tho&amp;aacute;ng nh&amp;igrave;n tin tức tr&amp;ecirc;n bia, ngữ kh&amp;iacute; hờ hững c&amp;ocirc;ng bố&amp;hellip;&lt;/p&gt;\r\n\r\n&lt;p&gt;Trung ni&amp;ecirc;n nam tử vừa n&amp;oacute;i xong, kh&amp;ocirc;ng c&amp;oacute; g&amp;igrave; ngo&amp;agrave;i &amp;yacute; muốn tại đ&amp;aacute;m người tr&amp;ecirc;n quảng trường nhấc l&amp;ecirc;n trận trận ch&amp;acirc;m chọc tao động&lt;/p&gt;\r\n\r\n&lt;p&gt;Nguồn:&amp;nbsp;https://lightnovel.com.vn/truyen/dau-pha-thuong-khung-1/chuong-1-thien-tai-van-lac&lt;/p&gt;', '2024-11-21 18:07:59', '2024-11-21 18:07:59', 1, 1, 45, 1, 'time_1732183033_file_time_1722136017_file_banquyen.pdf'),
(32, 'Đế Bá', 'time_1732183650_file_đế bá.jpg', '&lt;p&gt;&lt;strong&gt;Văn &amp;aacute;n truyện:&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Ng&amp;agrave;n vạn năm trước, L&amp;yacute; Thất Dạ trồng xuống một c&amp;acirc;y Th&amp;uacute;y trăm vạn năm trước, L&amp;yacute; Thất Dạ nu&amp;ocirc;i một đầu c&amp;aacute; trăm vạn năm trước, L&amp;yacute; Thất Dạ thu dưỡng một c&amp;aacute;i tiểu c&amp;ocirc; nương.... ... ... ... ...H&amp;ocirc;m nay, L&amp;yacute; Thất Dạ tỉnh lại sau giấc ngủ, Th&amp;uacute;y Tr&amp;uacute;c tu luyện th&amp;agrave;nh thần linh, c&amp;aacute; ch&amp;eacute;p h&amp;oacute;a th&amp;agrave;nh Kim Long, tiểu c&amp;ocirc; nương trở th&amp;agrave;nh Cửu Giới Nữ l&amp;agrave; một c&amp;aacute;i dưỡng th&amp;agrave;nh cố sự, một c&amp;aacute;i bất tử Nh&amp;acirc;n tộc tiểu tử dưỡng th&amp;agrave;nh Y&amp;ecirc;u Thần, dưỡng th&amp;agrave;nh Ti&amp;ecirc;n th&amp;uacute;, dưỡng th&amp;agrave;nh Nữ Đế cố sự.&lt;/p&gt;\r\n\r\n&lt;p&gt;Nguồn:&amp;nbsp;https://lightnovel.com.vn/truyen/de-ba&lt;/p&gt;', '2024-11-21 18:07:53', '2024-11-21 18:07:53', 1, 1, 45, 1, 'time_1732183650_file_time_1722136017_file_banquyen.pdf'),
(33, 'Màn Ảnh', 'time_1732184077_file_hu chi.jpg', '&lt;p&gt;Năm 2001, Ho&amp;agrave;nh Điếm, 《&amp;nbsp;Phong V&amp;acirc;n 》 đo&amp;agrave;n l&amp;agrave;m phim.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;Đẹp trai. . .&amp;quot;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;Ai!&amp;quot;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;Lăn, ngươi nh&amp;igrave;n xem ngươi kia &amp;aacute;p chế dạng, ngươi c&amp;oacute; &amp;yacute; tốt ứng thanh&amp;nbsp;a!&amp;#39;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;Ch&amp;uacute;ng ta tỉnh Quảng Đ&amp;ocirc;ng b&amp;ecirc;n kia chỉ cần l&amp;agrave; nam, đều h&amp;ocirc;&amp;nbsp;đẹp trai.&amp;quot;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;Đ&amp;aacute;nh đổ đi, ngươi nh&amp;igrave;n xem người ta H&amp;aacute;ch Vận, kia mới gọi đẹp&amp;nbsp;trai &amp;zwnj; c&amp;oacute; được hay kh&amp;ocirc;ng, H&amp;aacute;ch Vận!&amp;quot;&lt;/p&gt;\r\n\r\n&lt;p&gt;Nguồn:&amp;nbsp;https://lightnovel.com.vn/truyen/cai-nay-vua-man-anh-chi-muon-khao-chung/chuong-0001-ta-chinh-la-kha-nghi-nam-nhan&lt;/p&gt;', '2024-11-21 18:07:49', '2024-11-21 18:07:49', 1, 1, 45, 1, 'time_1732184077_file_time_1722136017_file_banquyen.pdf'),
(34, 'Hưu Thư', 'time_1732184222_file_Hưu Thư.jpg', '&lt;p&gt;Trời cao kh&amp;ocirc;ng c&amp;oacute; giới hạn, biển m&amp;ecirc;nh m&amp;ocirc;ng kh&amp;ocirc;ng bờ bến .&lt;/p&gt;\r\n\r\n&lt;p&gt;Tiềm Long Đại Lục, Tứ Quốc giao hội v&amp;ugrave;ng đất, l&amp;agrave; một t&amp;ograve;a phồn hoa th&amp;agrave;nh nhỏ, t&amp;ecirc;n l&amp;agrave; An Dương th&amp;agrave;nh .&lt;/p&gt;\r\n\r\n&lt;p&gt;Th&amp;agrave;nh Bắc một chỗ u t&amp;iacute;ch v&amp;ugrave;ng đất, tọa lạc lấy một t&amp;ograve;a cũ n&amp;aacute;t lầu nhỏ .&lt;/p&gt;\r\n\r\n&lt;p&gt;L&amp;acirc;u c&amp;oacute; ba tầng, đại m&amp;ocirc;n rộng mở, từ b&amp;ecirc;n ngo&amp;agrave;i nh&amp;igrave;n thấy, cũng chỉ c&amp;oacute; một th&amp;acirc;n mang ăn mặc qu&amp;aacute;i dị, ni&amp;ecirc;n kỷ tuy nhi&amp;ecirc;n 20 tuổi cổ qu&amp;aacute;i thanh ni&amp;ecirc;n .&lt;/p&gt;\r\n\r\n&lt;p&gt;Thanh ni&amp;ecirc;n t&amp;ecirc;n l&amp;agrave; Lăng Hạo, ngay tại vừa rồi, hắn bị kh&amp;ocirc;ng biết t&amp;ecirc;n tồn tại đưa đến Tiềm Long Đại Lục, trong đầu c&amp;ograve;n nhiều ra một tin tức, để hắn l&amp;agrave;m Thi&amp;ecirc;n Đạo T&amp;agrave;ng Kinh C&amp;aacute;c nh&amp;acirc;n vi&amp;ecirc;n quản l&amp;yacute; .&lt;/p&gt;\r\n\r\n&lt;p&gt;Nguồn:&amp;nbsp;https://lightnovel.com.vn/truyen/thien-dao-tang-kinh-cac/chuong-1-vo-tu-thien-thu&lt;/p&gt;', '2024-11-21 18:07:43', '2024-11-21 18:07:43', 1, 1, 45, 1, 'time_1732184222_file_time_1722136017_file_banquyen.pdf'),
(35, 'Linh Vũ Thiên Hạ', 'time_1732184703_file_linh vũ thiên hạ.jpg', '&lt;p&gt;&amp;quot;Tr&amp;igrave; Dao, ta đối với ngươi như t&amp;igrave;nh cảm ch&amp;acirc;n th&amp;agrave;nh, ngươi v&amp;igrave; sao muốn g&amp;middot;iết ta?&amp;quot;&lt;/p&gt;\r\n\r\n&lt;p&gt;Trương Nhược Trần h&amp;eacute;t lớn một tiếng, nghĩ trước bổ nh&amp;agrave;o về ph&amp;iacute;a trước, &amp;eacute;p tới mạ v&amp;agrave;ng chế tạo giường &amp;quot;Kẽo kẹt&amp;quot; một tiếng, đột nhi&amp;ecirc;n ngồi dậy.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ph&amp;aacute;t hiện chỉ l&amp;agrave; một giấc mộng, Trương Nhược Trần mới thở ra một hơi thật d&amp;agrave;i, d&amp;ugrave;ng ống tay &amp;aacute;o đem mồ h&amp;ocirc;i tr&amp;ecirc;n tr&amp;aacute;n lau kh&amp;ocirc;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Kh&amp;ocirc;ng!&lt;/p&gt;\r\n\r\n&lt;p&gt;Đ&amp;acirc;y kh&amp;ocirc;ng phải l&amp;agrave; một giấc mộng!&lt;br /&gt;\r\nNguồn:&amp;nbsp;https://lightnovel.com.vn/truyen/van-co-than-de/chuong-1-tam-tram-nam-sau&lt;/p&gt;', '2024-11-21 18:07:38', '2024-11-21 18:07:38', 1, 1, 45, 1, 'time_1732184703_file_time_1722136017_file_banquyen.pdf'),
(36, 'Mê Vợ Không Lối Về', 'time_1732184909_file_mê vợ không lối về.jpg', '&lt;p&gt;&amp;quot;Thẩm Thi&amp;ecirc;n, lo lắng l&amp;agrave;m g&amp;igrave; đ&amp;acirc;y? Nhanh cho ta th&amp;ecirc;m tăng cường a!&amp;quot;&lt;/p&gt;\r\n\r\n&lt;p&gt;Một t&amp;ecirc;n nam tử cầm kiếm , vừa chống đối trước mắt Hồng Nh&amp;atilde;n lang h&amp;igrave;nh hung th&amp;uacute; , vừa cau m&amp;agrave;y qu&amp;aacute;t l&amp;ecirc;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;M&amp;agrave; một người kh&amp;aacute;c d&amp;aacute;ng người xinh đẹp nữ tử, cũng l&amp;agrave; một mặt thiếu ki&amp;ecirc;n nhẫn, Thẩm Thi&amp;ecirc;n h&amp;agrave;ng n&amp;agrave;y, ngoại trừ so&amp;aacute;i, thực sự l&amp;agrave; kh&amp;ocirc;ng c&amp;ograve;n g&amp;igrave; kh&amp;aacute;c!&lt;/p&gt;\r\n\r\n&lt;p&gt;L&amp;uacute;c n&amp;agrave;y, ph&amp;iacute;a sau một t&amp;ecirc;n tướng mạo anh tuấn, v&amp;oacute;c người cao gầy thiếu ni&amp;ecirc;n trong nh&amp;aacute;y mắt từ đờ ra b&amp;ecirc;n trong phục hồi tinh thần lại, vội v&amp;atilde; phất l&amp;ecirc;n một đạo ch&amp;oacute;i mắt &amp;aacute;nh s&amp;aacute;ng, gia tr&amp;igrave; qu&amp;aacute; khứ.&lt;/p&gt;\r\n\r\n&lt;p&gt;Hắn ch&amp;iacute;nh l&amp;agrave; Thẩm Thi&amp;ecirc;n, ng&amp;agrave;y h&amp;ocirc;m qua mới vừa hồn xuy&amp;ecirc;n qua hắn, đến hiện tại vẫn l&amp;agrave; một mặt cho&amp;aacute;ng v&amp;aacute;ng.&lt;br /&gt;\r\nNguồn:&amp;nbsp;https://truyenfull.io/me-vo-khong-loi-ve-982891/&lt;/p&gt;', '2024-11-21 18:07:30', '2024-11-21 18:07:30', 1, 1, 45, 1, 'time_1732184909_file_time_1722136017_file_banquyen.pdf'),
(37, 'Nhất Niệm Vĩnh Hằng', 'time_1732185076_file_nhất niệm vĩnh hằng.jpg', '&lt;p&gt;&lt;strong&gt;Văn &amp;aacute;n truyện:&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Nhất niệm th&amp;agrave;nh thương hải , nhất niệm h&amp;oacute;a ruộng d&amp;acirc;u. nhất niệm ch&amp;eacute;m ng&amp;agrave;n ma , nhất niệm tru vạn ti&amp;ecirc;n. duy ta niệm... vĩnh hằng. Truyện mới của đại thần Nhĩ Căn.&lt;/p&gt;\r\n\r\n&lt;p&gt;Nguồn:&amp;nbsp;https://lightnovel.com.vn/truyen/nhat-niem-vinh-hang&lt;/p&gt;', '2024-11-21 18:07:25', '2024-11-21 18:07:25', 1, 1, 45, 1, 'time_1732185076_file_time_1722136017_file_banquyen.pdf'),
(38, 'Phàm Nhân Tu Tiên', 'time_1732185313_file_phàm nhân tu tiên.jpg', '&lt;p&gt;&lt;strong&gt;Văn &amp;aacute;n truyện:&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Một c&amp;aacute;i b&amp;igrave;nh thường sơn th&amp;ocirc;n tiểu tử, ngẫu nhi&amp;ecirc;n dưới tiến v&amp;agrave;o nơi đ&amp;oacute; giang hồ tiểu m&amp;ocirc;n ph&amp;aacute;i, th&amp;agrave;nh một t&amp;ecirc;n k&amp;yacute; danh đệ tử.&lt;/p&gt;\r\n\r\n&lt;p&gt;Hắn lấy th&amp;acirc;n phận như vậy, như thế n&amp;agrave;o ở b&amp;ecirc;n trong m&amp;ocirc;n ph&amp;aacute;i đặt ch&amp;acirc;n, như thế n&amp;agrave;o đ&amp;atilde; b&amp;igrave;nh ổn dung tư chất tiến v&amp;agrave;o Tu Ti&amp;ecirc;n Giả h&amp;agrave;ng ngũ, từ đ&amp;oacute; tiếu ngạo trong tam giới&lt;/p&gt;\r\n\r\n&lt;p&gt;P/s: Một bộ truyện huyền thoại ai cũng &amp;iacute;t nhiều đ&amp;atilde; nghe qua t&amp;ecirc;n. Do thấy nhiều ae k&amp;ecirc;u bản dịch cũ ở mấy chỗ kh&amp;aacute;c kh&amp;aacute; tệ, l&amp;agrave;m mất c&amp;aacute;i hay của truyện, n&amp;ecirc;n m&amp;igrave;nh đăng mới lại bằng bản convert n&amp;agrave;y để b&amp;aacute;m s&amp;aacute;t nội dung truyện gốc nhất.&lt;/p&gt;\r\n\r\n&lt;p&gt;Nguồn:&amp;nbsp;https://lightnovel.com.vn/truyen/pham-nhan-tu-tien-truyen&lt;/p&gt;', '2024-11-21 18:07:18', '2024-11-21 18:07:18', 1, 1, 42, 1, 'time_1732185313_file_time_1722136017_file_banquyen.pdf'),
(39, 'Rể quý trời cho', 'time_1732185740_file_rể quý trời cho.jpg', '&lt;p&gt;Truyện Rể Qu&amp;yacute; Trời Cho của t&amp;aacute;c giả Quỷ Thượng Nh&amp;acirc;n với nội dung truyện xoay quanh nh&amp;acirc;n vật ch&amp;iacute;nh &amp;ndash; L&amp;acirc;m Thanh Diện: Khi đ&amp;oacute; mẹ L&amp;acirc;m Thanh Diện v&amp;igrave; muốn nắm quyền nh&amp;agrave; họ L&amp;acirc;m m&amp;agrave; kh&amp;ocirc;ng tiếc để anh g&amp;aacute;nh tội danh phản bội, anh phải rời khỏi gia tộc, l&amp;agrave;m con rể phế vật bị người người ch&amp;ecirc; cười ở Giang Th&amp;agrave;nh. Cho đến khi vợ bị ức hiếp, anh mới kh&amp;ocirc;ng thể tiếp tục điệu thấp nữa, lộ th&amp;acirc;n phận, vả mặt bọn người.&lt;/p&gt;\r\n\r\n&lt;p&gt;Nguồn:&amp;nbsp;https://truyenfull.io/re-quy-troi-cho-480197/&lt;/p&gt;', '2024-11-21 18:07:11', '2024-11-21 18:07:11', 1, 1, 42, 1, 'time_1732185740_file_time_1722136017_file_banquyen.pdf'),
(40, 'Thập Niên 50: Nhật Ký Làm Ruộng Làm Giàu Của Vợ Chồng Đại Lão', 'time_1732185863_file_thập niên 50.jpg', '&lt;p&gt;Đ&amp;acirc;y l&amp;agrave; một bộ truyện l&amp;agrave;m ruộng dựa tr&amp;ecirc;n nh&amp;acirc;n vật hư cấu v&amp;agrave; bối cảnh lịch sử kh&amp;ocirc;ng c&amp;oacute; thật. Chu Bắc Sơn v&amp;agrave; T&amp;ocirc; M&amp;atilde;n c&amp;ugrave;ng tang thi vương đồng quy vu tận, mở mắt ra đ&amp;atilde; trở th&amp;agrave;nh thập ni&amp;ecirc;n 50 một đ&amp;ocirc;i vừa mất đi người th&amp;acirc;n vị h&amp;ocirc;n phu vị h&amp;ocirc;n th&amp;ecirc;. Cũng may, dị năng cũng theo, thả xem hai vợ chồng như thế n&amp;agrave;o tru&amp;acirc;n lương, vượt qua nạn đ&amp;oacute;i, tại đ&amp;acirc;y thiếu ăn thiếu mặc ni&amp;ecirc;n đại, lăng l&amp;agrave; l&amp;agrave;m cho cả gia đ&amp;igrave;nh ấm no hạnh ph&amp;uacute;c, nh&amp;acirc;n sinh xuất sắc rực rỡ.&amp;nbsp;&lt;br /&gt;\r\n&lt;br /&gt;\r\nCh&amp;uacute; &amp;yacute;: Đ&amp;acirc;y l&amp;agrave; c&amp;acirc;u truyện m&amp;agrave; bối cảnh v&amp;agrave; nh&amp;acirc;n vật hư cấu, l&amp;agrave; t&amp;aacute;c phẩm đầu tay, t&amp;aacute;c giả lại l&amp;agrave; d&amp;acirc;n ghiền truyện Trung, n&amp;ecirc;n đ&amp;ocirc;i khi sẽ c&amp;oacute; một số từ ngữ H&amp;aacute;n Việt, mong đọc giả th&amp;ocirc;ng cảm.&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;Nguồn:&amp;nbsp;https://truyenfull.io/thap-nien-50-nhat-ky-lam-ruong-lam-giau-cua-vo-chong-dai-lao/&lt;/p&gt;', '2024-11-21 18:07:05', '2024-11-21 18:07:05', 1, 1, 42, 1, 'time_1732185863_file_time_1722136017_file_banquyen.pdf'),
(41, 'Thiên Tai: Toàn Dân Chạy Nạn, Ta Có Hàng Tỷ Vật Tư', 'time_1732186084_file_thiên tai.jpg', '&lt;p&gt;To&amp;agrave;n cầu bỗng nhi&amp;ecirc;n mất điện kh&amp;ocirc;ng r&amp;otilde; nguy&amp;ecirc;n nhan, trường học ngừng học, nh&amp;agrave; m&amp;aacute;y ngừng l&amp;agrave;m việc, vận chuyển đường biển, đường bộ, đường h&amp;agrave;ng kh&amp;ocirc;ng đều bị gi&amp;aacute;n đoạn, tất cả vật tư trong si&amp;ecirc;u thị đều bị tranh mua hết sạch, một đợt nguy cơ lớn hơn lặng lẽ b&amp;ugrave;ng nổ.&lt;br /&gt;\r\n&lt;br /&gt;\r\nNhiệt độ tăng cao li&amp;ecirc;n tục, mặt trời tỏa ra ph&amp;oacute;ng xạ, b&amp;atilde;o c&amp;aacute;t dồn dập, trời gi&amp;aacute;ng thi&amp;ecirc;n thạch, động đất, mưa to, s&amp;oacute;ng thần, nạn s&amp;acirc;u bệnh, c&amp;uacute;m &amp;ocirc;n dịch, virus biến dị, zombie bao v&amp;acirc;y, sinh vật dị biến, b&amp;atilde;o tuyết cực h&amp;agrave;n, n&amp;uacute;i băng tan chảy... Trong cơn khủng hoảng nghi&amp;ecirc;m trọng, sinh mạng con người kh&amp;ocirc;ng được bảo đảm, tất cả ph&amp;aacute;p luật c&amp;ugrave;ng trật tự x&amp;atilde; hội đều sụp đổ.&lt;br /&gt;\r\n&lt;br /&gt;\r\nKhương Tư ki&amp;ecirc;n tr&amp;igrave; mười năm ở thời kỳ tận thế, b&amp;agrave; nội bị gi&amp;oacute; lốc cuốn đi, &amp;ocirc;ng nội bị s&amp;oacute;ng thần k&amp;eacute;o mất, cha bị zombie cắn chết, mẹ sinh bệnh tự s&amp;aacute;t, c&amp;ograve;n c&amp;ocirc; th&amp;igrave; bị kẻ biến dị giết.&lt;/p&gt;\r\n\r\n&lt;p&gt;Nguồn:&amp;nbsp;https://truyenfull.io/thien-tai-toan-dan-chay-nan-ta-co-hang-ty-vat-tu/&lt;/p&gt;', '2024-11-21 18:04:24', '2024-11-21 18:04:24', 1, 1, 42, 1, 'time_1732186084_file_time_1722136017_file_banquyen.pdf'),
(42, 'Tiên Nghịch', 'time_1732186247_file_tiên nghịch.jpg', '&lt;p&gt;&lt;strong&gt;Văn &amp;aacute;n truyện:&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;- Tu ti&amp;ecirc;n l&amp;agrave; nghịch thi&amp;ecirc;n hay thuận thi&amp;ecirc;n, l&amp;agrave; v&amp;ocirc; t&amp;igrave;nh hay hữu t&amp;igrave;nh? H&amp;atilde;y c&amp;ugrave;ng d&amp;otilde;i theo bước ch&amp;acirc;n của Vương L&amp;acirc;m qua Truyện Ti&amp;ecirc;n Nghịch để biết r&amp;otilde; những điều n&amp;agrave;y bạn nh&amp;eacute;!&lt;/p&gt;\r\n\r\n&lt;p&gt;--------------------&lt;/p&gt;\r\n\r\n&lt;p&gt;Nguồn:&amp;nbsp;https://lightnovel.com.vn/truyen/tien-nghich-1&lt;/p&gt;', '2024-11-21 18:03:43', '2024-11-21 18:03:43', 1, 1, 42, 1, 'time_1732186247_file_ban-cam-ket-chiu-trach-nhiem(mau).pdf'),
(43, 'Thần Thông Đạo Sĩ', 'time_1732186534_file_trừ tà.jpg', '&lt;p&gt;Trần Ho&amp;agrave;ng Kim vừa sinh đ&amp;atilde; phạm mệnh yếu, bệnh tật li&amp;ecirc;n miền, lại khi&amp;ecirc;́n cả d&amp;ograve;ng họ chết bất đắc kỳ tử. &amp;Ocirc;ng nội d&amp;ugrave;ng thọ số c&amp;ograve;n lại t&amp;iacute;nh một quẻ, vãn hồi mọi thứ bằng c&amp;aacute;ch cột nh&amp;acirc;n duy&amp;ecirc;n giữa Trần Ho&amp;agrave;ng Kim với Diệp Điền T&amp;iacute;nh. Từ đ&amp;oacute;, dòng họ được b&amp;igrave;nh y&amp;ecirc;n. Trần Hoàng Kim học hết y b&amp;aacute;t c&amp;uacute;a &amp;ocirc;ng nội, trở th&amp;agrave;nh truyền thừa duy nhất của đạo sĩ Th&amp;aacute;nh Gai.&lt;br /&gt;\r\n&lt;br /&gt;\r\nNhưng 20 năm sau, nh&amp;agrave; họ Diệp hủy bỏ h&amp;ocirc;n ước, khiến vận mệnh của Trần Ho&amp;agrave;ng Kim bị đảo loạn.&lt;br /&gt;\r\n&lt;br /&gt;\r\nĐ&amp;atilde; đến l&amp;uacute;c, anh phải xuống n&amp;uacute;i, tự tay bắt lấy h&amp;ocirc;n nh&amp;acirc;n n&amp;agrave;y!&lt;/p&gt;\r\n\r\n&lt;p&gt;Nguồn:&amp;nbsp;https://truyenfull.io/ngu-phap-tru-ta-than-thong-dao-si/&lt;/p&gt;', '2024-11-21 18:03:26', '2024-11-21 18:03:26', 1, 1, 42, 1, 'time_1732186534_file_time_1722136017_file_banquyen.pdf'),
(44, 'Từ Giới Giải Trí Đến Nhà Giàu Số 1', 'time_1732186653_file_Từ giới giải trí.jpg', '&lt;p&gt;Dương Lỗi đột nhi&amp;ecirc;n nhận được một hệ thống đầy b&amp;iacute; ẩn, cho ph&amp;eacute;p anh đổi to&amp;agrave;n bộ đồ vật bằng danh vọng. Mặc d&amp;ugrave; anh theo chủ nghĩa v&amp;ocirc; thần, nhưng sự xuất hiện của hệ thống đ&amp;atilde; đảo lộn tưởng tượng của anh.&lt;br /&gt;\r\n&lt;br /&gt;\r\nMột ng&amp;agrave;y, khi anh đang học tại đại học, anh đột nhi&amp;ecirc;n bị ngất xỉu v&amp;agrave; sau đ&amp;oacute; nhận được hệ thống. Điều n&amp;agrave;y dẫn anh đến suy nghĩ rằng hai sự kiện n&amp;agrave;y c&amp;oacute; li&amp;ecirc;n quan đến nhau.&lt;br /&gt;\r\n&lt;br /&gt;\r\nKhi anh thấy những biểu tượng tr&amp;ecirc;n giao diện thay đổi, anh nhanh ch&amp;oacute;ng nhận thấy rằng anh c&amp;oacute; thể tiến v&amp;agrave;o một thế giới tiểu thuyết. Trong gi&amp;acirc;y l&amp;aacute;t, anh được đưa đến một thế giới ho&amp;agrave;n to&amp;agrave;n mới.&lt;/p&gt;\r\n\r\n&lt;p&gt;Nguồn:&amp;nbsp;https://truyenfull.io/tu-gioi-giai-tri-den-nha-giau-so-1/&lt;/p&gt;', '2024-11-21 18:06:52', '2024-11-21 18:06:52', 1, 0, 42, 1, 'time_1732186653_file_time_1722136017_file_banquyen.pdf'),
(45, 'Vạn Cổ Chí Tôn', 'time_1732186838_file_vạn cổ chí tôn.jpg', '&lt;p&gt;800 năm trước, Minh Đế chi tử Trương Nhược Trần, bị vị h&amp;ocirc;n th&amp;ecirc; của hắn Tr&amp;igrave; Dao c&amp;ocirc;ng ch&amp;uacute;a giết chết, thi&amp;ecirc;n ki&amp;ecirc;u một đời, liền như vậy ng&amp;atilde; xuống. T&amp;aacute;m trăm năm sau, Trương Nhược Trần một lần nữa sống lại, lại ph&amp;aacute;t hiện vị h&amp;ocirc;n th&amp;ecirc; đ&amp;atilde; từng giết chết hắn, đ&amp;atilde; thống nhất C&amp;ocirc;n L&amp;ocirc;n Giới, mở ra Đệ Nhất Trung Ương đế quốc, được xưng &amp;quot;Tr&amp;igrave; Dao Nữ Ho&amp;agrave;ng&amp;quot; .&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;Tr&amp;igrave; Dao Nữ Ho&amp;agrave;ng &amp;mdash;&amp;mdash; thống ngự thi&amp;ecirc;n hạ, uy l&amp;acirc;m b&amp;aacute;t phương; thanh xu&amp;acirc;n m&amp;atilde;i m&amp;atilde;i, bất tử bất diệt. Trương Nhược Trần đứng ở Chư Ho&amp;agrave;ng Từ Đường ở ngo&amp;agrave;i, nh&amp;igrave;n Tr&amp;igrave; Dao Nữ Ho&amp;agrave;ng tượng thần, trong l&amp;ograve;ng bốc ch&amp;aacute;y l&amp;ecirc;n hừng hực cừu hận liệt diễm, &amp;quot;Đợi ta tr&amp;ugrave;ng tu mười ba năm, d&amp;aacute;m gọi Nữ Ho&amp;agrave;ng dưới Ho&amp;agrave;ng Tuyền&amp;quot; .&lt;/p&gt;\r\n\r\n&lt;p&gt;Nguồn:&amp;nbsp;https://lightnovel.com.vn/truyen/van-co-than-de&lt;/p&gt;', '2024-11-21 18:02:35', '2024-11-21 18:02:35', 1, 1, 42, 1, 'time_1732186838_file_time_1722136017_file_banquyen.pdf');
